    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        // TODO add your handling code here:
        int id_objeto = Integer.parseInt(spnbuscarobjetoid.getValue().toString());
        Objeto obj = new DaoObjeto().Buscar(id_objeto);
        if (obj!= null) {
            JOptionPane.showMessageDialog(null, "Se encontro objeto");
            spnId_objeto.setValue(obj.getId_objeto());
            txtDescripcion.setText(obj.getDescripcion_objeto());
            txtTipo.setText(obj.getTipo().toString());
            txtColor.setText(obj.getColor().toString());
            txtFecha.setText(obj.getFecha_encontrado().toString());
            txtEncontrado.setText(obj.getEncontrado().getRut_encontrado());
            txtRecibido.setText(obj.getRecibido().getRut_recibido());
            spnid_sala.setValue(obj.getSala().getId_sala());
        } else {
            JOptionPane.showMessageDialog(null, "No se encuentra objeto");
        }

    }//GEN-LAST:event_btnBuscarActionPerformed

